import Modal from 'react-modal';
import { Container, TransactionTypeContainer, RadioBox } from './styles';
import closeImg from '../../assets/closebutton.svg';
import incomes from "../../assets/incomes.svg";
import expenses from "../../assets/expenses.svg";
import { useState } from 'react';

interface NewTransactionModalProps {
    isOpen: boolean;
    onRequestClose: () => void;
}

export function NewTransactionModal({ isOpen, onRequestClose }: NewTransactionModalProps) {
    const [type, setType] = useState('deposit');
    
    return (
        <Modal 
            isOpen={isOpen}
            onRequestClose={onRequestClose}
            overlayClassName="react-modal-overlay"
            className="react-modal-content"
        >

            <button type='button' onClick={onRequestClose} className="react-modal-close">
                <img src={closeImg} alt="Cancelar" />
            </button>

            <Container>
                <h2>Cadastrar Transação</h2>
                <input placeholder='Título' />
                <input type="number" placeholder='Valor' />
                
                <TransactionTypeContainer>
                    <RadioBox type='button' onClick={() => { setType('deposit')}}>
                         <img src={incomes} alt="Entrada" />
                         <span>Entrada</span>
                    </RadioBox>

                    <RadioBox type='button' onClick={() => { setType('withdraw')}}> 
                        <img src={expenses} alt="Saída" />
                        <span>Saída</span>
                    </RadioBox>
                </TransactionTypeContainer>

                <input placeholder='Categoria' />
                <button type='submit'>Cadastrar</button>
            </Container>
        </Modal>
    )
}
